import React, { useEffect, useState } from 'react';
import { Map, Marker } from 'pigeon-maps';
import axios from 'axios';

const App = () => {
  const [locations, setLocations] = useState({});
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentLocation, setCurrentLocation] = useState([0, 0]); // Initialize with a default location
  const [clickedUsername, setClickedUsername] = useState(null); // State to hold the clicked username

  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get_locations');
        setLocations(response.data);

        // Update current location if logged in
        if (loggedIn) {
          const userLocation = response.data[username];
          if (userLocation) {
            setCurrentLocation(JSON.parse(userLocation));
          }
        }
      } catch (error) {
        console.error("Error fetching locations", error);
      }
    };

    if (loggedIn) {
      fetchLocations();
      const interval = setInterval(fetchLocations, 3000);
      return () => clearInterval(interval);
    }
  }, [loggedIn, username]);

  const handleRegister = async () => {
    try {
      await axios.post('http://localhost:5000/register', { username, password });
      alert('Registration successful!');
    } catch (error) {
      console.error("Error during registration", error);
    }
  };
  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      if (response.data.status === 'success') {
        setLoggedIn(true);

        // Fetch and set initial current location after login
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const { latitude, longitude } = position.coords;
            const location = [latitude, longitude];
            handleUpdateLocation(location); // Update backend with current location
            setCurrentLocation(location);   // Update frontend state with current location
          },
          (error) => {
            console.error("Error getting current position", error);
          }
        );
      } else {
        alert('Login failed!');
      }
    } catch (error) {
      console.error("Error during login", error);
    }
  };

  const handleUpdateLocation = async (location) => {
    try {
      await axios.post('http://localhost:5000/update_location', { username, location });
      setCurrentLocation(location); // Update frontend state with current location
    } catch (error) {
      console.error("Error updating location", error);
    }
  };


  const handleMarkerClick = (user) => {
    setClickedUsername(user);
  };

  return (
    <div>
      {!loggedIn ? (
        <div>
          <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleRegister}>Register</button>
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div>
          <h2>Map</h2>
          <Map
              height={600}
              defaultCenter={currentLocation} // Use currentLocation as defaultCenter
              defaultZoom={11}
              onClick={({ latLng }) => handleUpdateLocation(latLng)}
            >
              {Object.entries(locations).map(([user, loc]) => {
                const [lat, lng] = JSON.parse(loc);
                return (
                  <Marker key={user} width={50} anchor={[lat, lng]} onClick={() => handleMarkerClick(user)}>
                    {clickedUsername === user && (
                      <div style={{ backgroundColor: 'white', padding: 5, borderRadius: 5, boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
                        {user}
                      </div>
                    )}
                  </Marker>
                );
              })}
              <Marker width={50} anchor={currentLocation} />
            </Map>
        </div>
      )}
    </div>
  );
};

export default App;
