import React, { useEffect, useState } from 'react';
import { Map, Marker, Overlay } from 'pigeon-maps';
import axios from 'axios';
import io from 'socket.io-client';
import './App.css'; // Import the CSS file

const socket = io('http://localhost:3000'); // Adjust the URL to your server's

const App = () => {
  const [locations, setLocations] = useState({});
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentLocation, setCurrentLocation] = useState([0, 0]);
  const [path, setPath] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [items, setItems] = useState([]);
  const [distances, setDistances] = useState({});
  const [collectedItemsCount, setCollectedItemsCount] = useState({});

  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get_locations');
        setLocations(response.data);

        if (loggedIn) {
          const userLocation = response.data[username];
          // Assuming you handle user location update here
        }
      } catch (error) {
        console.error('Failed to fetch locations:', error);
      }
    };

    fetchLocations();

    socket.on('leaderboardUpdate', (leaderboardData) => {
      setCollectedItemsCount(leaderboardData);
    });

    return () => {
      socket.off('leaderboardUpdate');
    };
  }, [loggedIn, username]);

  // Combine, sort, and render leaderboard entries
  const renderLeaderboard = () => {
    const staticItems = [
      { user: 'siva', count: 5 },
      { user: 'Navale', count: 7 },
    ];

    const combinedItems = [
      ...Object.entries(collectedItemsCount).map(([user, count]) => ({ user, count })),
      ...staticItems,
    ];

    const sortedItems = combinedItems.sort((a, b) => b.count - a.count);

    return (
      <div className="popup-section leaderboard-section">
        <h3>Leaderboard - Items Collected</h3>
        <ul>
          {sortedItems.map(({ user, count }) => (
            <li key={user}>{user}: {count} item(s)</li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="App">
      {!loggedIn ? (
        <div className="login-register-container">
          <h1 className="game-app-heading">Game-App</h1>
          <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleRegister}>Register</button>
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div>
          <h2>Map</h2>
          <Map height={600} defaultCenter={currentLocation} defaultZoom={11}>
            {Object.entries(locations).map(([user, loc]) => {
              try {
                const [lat, lng] = JSON.parse(loc);
                return (
                  <Marker
                    key={user}
                    width={50}
                    anchor={[lat, lng]}
                    onClick={() => setSelectedLocation({ username: user, location: [lat, lng] })}
                  />
                );
              } catch (e) {
                console.error(`Error parsing location for user ${user}:`, loc, e);
                return null;
              }
            })}

            {items.map(item => (
              <Marker
                key={item.id}
                width={50}
                anchor={item.location}
                color="blue"
                onClick={() => {
                  alert(`You found an item at ${item.location[0].toFixed(4)}, ${item.location[1].toFixed(4)}!`);
                  setItems(prevItems => prevItems.filter(i => i.id !== item.id));
                  const newCount = { ...collectedItemsCount };
                  newCount[username] = (newCount[username] || 0) + 1;
                  setCollectedItemsCount(newCount);
                }}
              />
            ))}

            <div className="popup-section leaderboard-section">
              <h3>Leaderboard - Items Collected</h3>
              <ul>
                {Object.entries(collectedItemsCount)
                  .sort(([, countA], [, countB]) => countB - countA)
                  .map(([user, count]) => (
                    <li key={user}>{user}: {count} item(s)</li>
                  ))}
                  <li>siva: 5 item(s)</li>
                  <li>Navale:  item(s)</li>
                </ul>
                </div>

                  <div className="popup-section distances-section">
                    <h3>Distances:</h3>
                    <ul>
                      {Object.entries(distances).map(([key, distance]) => (
                        <li key={key}>{`${key}: ${distance.toFixed(2)} meters`}</li>
                      ))}
                    </ul>
                  </div>

                  {selectedLocation && selectedLocation.location && (
                    <Overlay anchor={selectedLocation.location} offset={[120, 79]}>
                      <div className="selected-location-overlay">
                        <strong>{selectedLocation.username}</strong><br />
                        Lat: {selectedLocation.location[0].toFixed(4)}, Lng: {selectedLocation.location[1].toFixed(4)}
                      </div>
                    </Overlay>
                  )}

                  <Polyline positions={path} />
                </Map>
              </div>
            )}
          </div>
        );
      };

      export default App;
